    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Goodies {% endblock %}
{% block link %}
    <link rel=\"stylesheet\" href=\"{{ asset('css/plugins/dataTables.bootstrap5.min.css') }}\" >
{% endblock %}
{% block body %}
    <h1>Goodies </h1> 
    <a href=\"{{ path('app_goodies_new') }}\" class=\"btn btn-primary\">Nouveau Goodie</a>
    <input type=\"text\" id=\"search\" placeholder=\"Rechercher dans le tableau...\" class=\"form-control mb-3\">
    <table id=\"dom-jqry\" class=\"table table-striped table-bordered nowrap\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Quantity</th>
                <th>Image</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for goody in goodies %}
            <tr>
                <td>{{ goody.id }}</td>
                <td>{{ goody.Nom }}</td>
                <td>{{ goody.Quantity }}</td>
                <td>
                    <img width=\"100\" src=\"{{asset('uploads/imgesGoodies/'~goody.Image)}}\">
                </td>
                <td>
                    <a href=\"{{ path('app_goodies_show', {'id': goody.id}) }}\">show</a>
                    <a href=\"{{ path('app_goodies_edit', {'id': goody.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    {% block javascripts %}
    <script src=\"https://code.jquery.com/jquery-3.6.0.min.js\"></script>
    <script>
        \$(document).ready(function(){
            \$(\"#search\").on(\"keyup\", function() {
                var value = \$(this).val().toLowerCase();
                \$(\"#dom-jqry tbody tr\").filter(function() {
                    \$(this).toggle(\$(this).text().toLowerCase().indexOf(value) > -1)
                });
            });
        });
    </script>
{% endblock %}
{% endblock %}
", "goodies/index.html.twig", "/Users/hantour/template_dashboard/templates/goodies/index.html.twig");
