    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Operation{% endblock %}
{% block link %}
    <link rel=\"stylesheet\" href=\"{{ asset('css/plugins/dataTables.bootstrap5.min.css') }}\" >
{% endblock %}
{% block body %}
    <h1>Liste des opérations </h1>

    <a href=\"{{ path('app_operation_new') }}\" class=\"btn btn-primary\">Nouvelle opération</a>

    <input type=\"text\" id=\"search\" placeholder=\"Rechercher dans le tableau...\" class=\"form-control mb-3\">
    <table id=\"dom-jqry\" class=\"table table-striped table-bordered nowrap\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Statut</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for operation in operations %}
            <tr>
                <td>{{ operation.id }}</td>
                <td>{{ operation.nom }}</td>
                <td>{{ operation.statut }}</td>
                <td>

                    <button class=\"btn avtar avtar-xs btn-light-success\"><a style=\"text-decoration: none;color:inherit;\" href=\"{{ path('app_operation_show', {'id': operation.id}) }}\"><i class=\"ti ti-eye\"></i></a></button>
                    <button class=\"btn avtar avtar-xs btn-light-danger\"><a style=\"text-decoration: none;color:inherit;\" href=\"{{ path('app_operation_edit', {'id': operation.id}) }}\"><i class=\"ti ti-pencil\"></i></a></button>
                    
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"4\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}

{% block javascripts %}
    <script src=\"https://code.jquery.com/jquery-3.6.0.min.js\"></script>
    <script>
        \$(document).ready(function(){
            \$(\"#search\").on(\"keyup\", function() {
                var value = \$(this).val().toLowerCase();
                \$(\"#dom-jqry tbody tr\").filter(function() {
                    \$(this).toggle(\$(this).text().toLowerCase().indexOf(value) > -1)
                });
            });
        });
    </script>
{% endblock %}", "operation/index.html.twig", "/Users/hantour/template_dashboard/templates/operation/index.html.twig");
    }
